#include <bits/stdc++.h>using namespace std;typedef long long ll;int main(){    int t;    cin>>t;    while(t--){        ll n,x;        cin>>n>>x;        vector<vector<ll>> a(n,vector<ll>(3,0));        ll bt=LLONG_MIN;        ll f=0;        for(int i=0;i<n;i++){            cin>>a[i][0]>>a[i][1]>>a[i][2];            f+=a[i][0]*(a[i][1]-1);            ll tot=a[i][0]*a[i][1]-a[i][2];            bt=max(bt,tot);        }        if(f>=x){            cout<<0<<endl;            continue;        }        if(bt<=0){            cout<<-1<<endl;            continue;        }        ll rem=x-f;        ll ans=(rem+bt-1)/bt;        cout<<ans<<endl;    }}