#include <bits/stdc++.h>using namespace std;typedef long long ll;int main(){    int t;    cin>>t;    while(t--){        int n;        cin>>n;        vector<ll> a(n);        ll num=n;        for(int i=0;i<n;i++){            a[i]=i+1;        }        if(n%2==0){            for(int i=1;i<n-2;i+=2){                swap(a[i],a[i+1]);            }            swap(a[0],a[n-1]);            for(int i=0;i<n;i++){                cout<<a[i]<<" ";            }        }        else{            for(int i=1;i<n-1;i+=2){                swap(a[i],a[i+1]);            }            swap(a[0],a[n-1]);            for(int i=0;i<n;i++){                cout<<a[i]<<" ";            }        }        cout<<endl;    }}