#include <bits/stdc++.h>using namespace std;typedef long long ll;int main(){    int t;    cin>>t;    while(t--){        int n;        cin>>n;        vector<int> a(n);        for(int i=0;i<n;i++) cin>>a[i];        vector<int> b(n);        int num=n;        for(int i=0;i<n;i++){            b[i]=num;            num--;        }        int j=0;        while(a[j]==b[j]) j++;        if(j<n){            int k=0;            int target=n-j;            for(int i=0;i<n;i++){                if(a[i]==target){                    k=i;                    break;                }            }            reverse(a.begin()+j,a.begin()+k+1);        }        // cout<<"ans=";        for(int i=0;i<n;i++) cout<<a[i]<<" ";        cout<<endl;    }}