#include <bits/stdc++.h>using namespace std;typedef long long ll;int main(){    int t;    cin>>t;    while(t--){        int n;        cin>>n;        vector<int> a(n);        vector<int> b(n);        for(int i=0;i<n;i++) cin>>a[i];        for(int i=0;i<n;i++) cin>>b[i];        for(int i=1;i<n;i++){            b[i]+=b[i-1];        }        sort(a.begin(),a.end());        map<int,int> mp;        for(int i=0;i<n;i++){            mp[b[i]]=i;        }        ll ans=0;        int i=0;        while(i<n){            int cnt=n-i;            auto it=mp.upper_bound(cnt);            if(it==mp.begin()){                i++;                continue;            }            it--;            int num=it->second+1;            ans=max(ans,1LL*a[i]*num);            i++;        }        cout<<ans<<endl;    }}