#include <bits/stdc++.h>using namespace std;typedef long long ll;int main() {	// your code goes here    int t;    cin>>t;    while(t--){        ll n,x,y;        cin>>n>>x>>y;        vector<ll> a(n);        for(int i=0;i<n;i++){            cin>>a[i];        }        ll b=0;        for(int i=0;i<n;i++){            b+=(a[i]/x)*y;        }        ll ans=0;        for(int i=0;i<n;i++){            ans=max(ans,a[i]+(b-(a[i]/x)*y));        }        cout<<ans<<endl;    }}