#include <bits/stdc++.h>using namespace std;typedef long long ll;int main() {	// your code goes here    int t;    cin>>t;    while(t--){        int n,k;        cin>>n>>k;        vector<int> msk(n,0);        for(int i=0;i<k;i++){            string s;            cin>>s;            for(int j=0;j<n;j++) {                msk[j]|=(1<<(s[j]-'a'));            }        }        vector<int> divs;        for(int i=1;i*i<=n;i++){            if(n%i==0){                divs.push_back(i);                if(i*i!=n) divs.push_back(n/i);            }        }        sort(divs.begin(),divs.end());        for(int d:divs){            string res="";            bool ok=true;            for(int i=0;i<d;i++){                int common=(1<<26)-1;                for(int j=i;j<n;j+=d) {                    common&=msk[j];                }                if(common==0){                    ok=false;                    break;                }                for(int c=0;c<26;c++){                    if((common>>c)&1){                        res+=(char)('a'+c);                        break;                    }                }            }            if(ok){                for(int i=0;i<n/d;i++) cout<<res;                cout<<endl;                break;            }        }    }}