#include <bits/stdc++.h>using namespace std;typedef long long ll;int main() {	// your code goes here    int t;    cin>>t;    while(t--){        int n,m;        cin>>n>>m;        vector<vector<ll>> a(n,vector<ll> (m,0));        ll cnt=0;        for(int i=0;i<n;i++){            for(int j=0;j<m;j++){                cin>>a[i][j];                cnt+=a[i][j];            }        }        for(int i=0;i<m;i++){            for(int j=n-2;j>=0;j--){                a[j][i]+=a[j+1][i];            }        }        string res="";        int i=0,j=0;        ll ans=(cnt/2)*(cnt-cnt/2);        ll num=cnt/2;        while(i<n&&j<m){            if(num-a[i][j]>=0){                res+="R";                num-=a[i][j];                j++;            }            else{                res+="D";                i++;            }        }        while(i<n){            res+="D";            i++;        }        while(j<m){            res+="R";            j++;        }        cout<<ans<<endl;        cout<<res<<endl;    }}