#include<bits/stdc++.h>using namespace std;typedef long long ll;struct Trie{    struct Node{        int nxt[26];        Node(){            memset(nxt,-1,sizeof(nxt));        }    };    vector<Node> t;    ll uniqueCount;    Trie(){        t.push_back(Node());        uniqueCount=0;    }};Â int main(){    ios::sync_with_stdio(0);    cin.tie(0);        string s,c;        int k;        cin>>s>>c>>k;        vector<int> pre(s.size()+1,0);        for(int i=1;i<=s.size();i++){            if(c[s[i-1]-'a']=='0')pre[i]=1;        }        for(int i=1;i<=s.size();i++){            pre[i]+=pre[i-1];        }        Trie tr;        for(int i=1;i<=s.size();i++){            int cur=0;            for(int j=i;j<=s.size();j++){                if(pre[j]-pre[i-1]<=k){                    int x=s[j-1]-'a';                    if(tr.t[cur].nxt[x]==-1){                        tr.t[cur].nxt[x]=tr.t.size();                        tr.t.push_back(Trie::Node());                        tr.uniqueCount++;                    }                    cur=tr.t[cur].nxt[x];                }            }        }        cout<<tr.uniqueCount<<endl;}