#include <bits/stdc++.h>using namespace std;typedef long long ll;bool check(ll num,vector<ll>& q,vector<ll>& p,vector<ll>& n,ll r){    bool flag=true;    ll cost=0;    for(int i=0;i<3;i++){        ll need=max(0LL,n[i]*num-q[i]);        cost+=need*p[i];    }    if(cost<=r)return true;    return false;}int main(){    string s;    cin>>s;    vector<ll> q(3,0);    vector<ll> p(3,0);    vector<ll> n(3,0);    for(int i=0;i<3;i++){        cin>>q[i];    }    for(int i=0;i<3;i++){        cin>>p[i];    }    ll r;    cin>>r;    ll cost=0;    for(int i=0;i<s.size();i++){        if(s[i]=='B')n[0]++;        else if(s[i]=='S')n[1]++;        else n[2]++;    }    ll lo=0;    ll hi=1e14;    ll res=0;    while(lo<=hi){        ll mid=(lo+hi)/2;        if(check(mid,q,p,n,r)){            res=mid;            lo=mid+1;        }        else hi=mid-1;    }    cout<<res<<endl;    return 0;}