#include <bits/stdc++.h>using namespace std;typedef long long ll;ll xor1ton(ll n){    if(n==0) return 0;    if(n%4==1) return 1;    if(n%4==2) return n+1;    if(n%4==3) return 0;    return n;}int main(){        ll n;        cin>>n;        vector<ll> a(n);        ll xr=0;        for(ll i=0;i<n;i++){            cin>>a[i];            xr^=a[i];          }        ll ans=xr;        for(ll i=1;i<=n;i++){            if((n/i)%2==0){                ans^=xor1ton((n%i));            }            else{                ans^=xor1ton(i-1);                ans^=xor1ton((n%i));            }        }        cout<<ans<<endl;}