#include <bits/stdc++.h>using namespace std;const int mod = 998244353;typedef long long ll;int main(){    ll n,k;    cin>>n>>k;    vector<ll> a(n);    for(int i=0;i<n;i++){        cin>>a[i];    }    for(int i=1;i<n;i++){        a[i]=a[i]+a[i-1];    }    ll prev=0;    ll sum=0;    for(int i=0;i<n-k+1;i++){        if(i>0)prev=a[i-1];        sum+=a[i+k-1]-prev;    }    cout<<fixed<<setprecision(10)<<(double)sum/(n - k + 1)<<endl;}