#include <bits/stdc++.h>using namespace std;typedef long long ll;int main(){        ll n;        cin>>n;        vector<pair<ll,ll>> a(n);        map<pair<ll,ll>,ll> mp;        for(int i=0;i<n;i++){            cin>>a[i].first>>a[i].second;            a[i].second=-a[i].second;            if(mp.find({a[i].first,a[i].second})!=mp.end()){                cout<<mp[{a[i].first,a[i].second}]<<" "<<i+1<<endl;                return 0;            }            mp[{a[i].first,a[i].second}]=i+1;        }          ll res1=-1;        ll res2=-1;        sort(a.begin(),a.end());        for(int i=0;i<n-1;i++){            if(abs(a[i].second)>=abs(a[i+1].second)){                res2=mp[{a[i].first,a[i].second}];                res1=mp[{a[i+1].first,a[i+1].second}];            }        }        cout<<res1<<" "<<res2<<endl;}